mapper.select:
select * from T_SYS_MENU


        NODEID=#{nodeid}

        AND NODELEVEL=#{nodelevel}

        AND NODECODE=#{nodecode}

        AND NODETEXT=#{nodetext}

        AND PATH=#{path}

        AND FNODEID=#{fnodeid}

        AND MTYPE=#{mtype}

        AND CREATETIME>=#{createtime}

        AND CODING=#{coding}

        AND COMMENT=#{comment}

select IFNULL(COUNT(NODEID),0) CNT,IFNULL(MAX(NODEID),0) MAXID,IFNULL(MIN(NODEID),0) MINID from T_SYS_MENU


        NODEID=#{nodeid}

        AND NODELEVEL=#{nodelevel}

        AND NODECODE=#{nodecode}

        AND NODETEXT=#{nodetext}

        AND PATH=#{path}

        AND FNODEID=#{fnodeid}

        AND MTYPE=#{mtype}

        AND CREATETIME>=#{createtime}

        AND CODING=#{coding}

        AND COMMENT=#{comment}

        AND CHECKED=#{checked}

select * from T_SYS_MENU


        NODEID=#{nodeid}

        AND NODELEVEL=#{nodelevel}

        AND NODECODE=#{nodecode}

        AND NODETEXT=#{nodetext}

        AND PATH=#{path}

        AND FNODEID=#{fnodeid}

        AND MTYPE=#{mtype}

        AND CREATETIME>=#{createtime}

        AND CODING=#{coding}

        AND COMMENT=#{comment}
 ORDER BY CODING

     LIMIT #{currentsize}, #{pagesize}

mapper.update:
update T_SYS_MENU
NODEID=#{nodeid}, NODELEVEL=#{nodelevel}, NODECODE=#{nodecode}, NODETEXT=#{nodetext}, PATH=#{path}, FNODEID=#{fnodeid}, SAVETIME=NOW(), CODING=#{coding}, COMMENT=#{comment},
where NODEID = #{nodeid}
