mapper.select:
select *
from T_SYS_STAFF


        USERID=#{userid}

        AND ROLEID=#{roleid}

        AND STATUS=#{status}

        AND COMPANYID=#{companyid}

        AND FATHERID=#{fatherid}

        AND USEREMAIL=#{useremail}

        AND USERMOBILEPHONE=#{usermobilephone}

        AND USERNAME=#{username}

        AND USERPASSWORD=#{userpassword}

        AND USERSTATUS=#{userstatus}

        AND USERTELEPHONE=#{usertelephone}

        AND USERTYPE=#{usertype}

        AND VENDORIDS=#{vendorids}

        AND JOBNUMBER=#{jobnumber}

        AND DEPARTMENT=#{department}

        AND SENDFLAG=#{sendflag}

        AND UFLAG=#{uflag}

        AND UNAME=#{uname}

        AND UFAX=#{ufax}

        AND NUSERTYPE=#{nusertype}

        AND CREATETIME>=#{createtime}


limit 1

select IFNULL(COUNT(USERID),0) CNT,IFNULL(MAX(USERID),0) MAXID,IFNULL(MIN(USERID),0) MINID from T_SYS_STAFF


        USERID=#{userid}

        AUTHID=#{authid}

        AND ROLEID=#{roleid}

        AND ROLELEVEL=#{rolelevel}

        AND STATUS=#{status}

        AND COMPANYID=#{companyid}

        AND FATHERID=#{fatherid}

        AND USEREMAIL=#{useremail}

        AND USERMOBILEPHONE=#{usermobilephone}

        AND USERNAME=#{username}

        AND USERPASSWORD=#{userpassword}

        AND USERSTATUS=#{userstatus}

        AND USERTELEPHONE=#{usertelephone}

        AND USERTYPE=#{usertype}

        AND VENDORIDS=#{vendorids}

        AND JOBNUMBER=#{jobnumber}

        AND DEPARTMENT=#{department}

        AND SENDFLAG=#{sendflag}

        AND UFLAG=#{uflag}

        AND UNAME=#{uname}

        AND UFAX=#{ufax}

        AND NUSERTYPE=#{nusertype}

        AND CREATETIME>=#{createtime}

select * from T_SYS_STAFF


        USERID=#{userid}

        AND AUTHID=#{authid}

        AND ROLEID=#{roleid}

        AND ROLELEVEL=#{rolelevel}

        AND STATUS=#{status}

        AND COMPANYID=#{companyid}

        AND FATHERID=#{fatherid}

        AND USEREMAIL=#{useremail}

        AND USERMOBILEPHONE=#{usermobilephone}

        AND USERNAME=#{username}

        AND USERPASSWORD=#{userpassword}

        AND USERSTATUS=#{userstatus}

        AND USERTELEPHONE=#{usertelephone}

        AND USERTYPE=#{usertype}

        AND VENDORIDS LIKE concat('%',#{vendorids},'%')

        AND JOBNUMBER=#{jobnumber}

        AND DEPARTMENT=#{department}

        AND SENDFLAG=#{sendflag}

        AND UFLAG=#{uflag}

        AND UNAME=#{uname}

        AND UFAX=#{ufax}

        AND NUSERTYPE=#{nusertype}

        AND CREATETIME>=#{createtime}
 ORDER BY SAVETIME DESC

     LIMIT #{currentsize}, #{pagesize}

mapper.update:
update T_SYS_STAFF
ROLEID=#{roleid}, ROLELEVEL=#{rolelevel}, STATUS=#{status}, COMPANYID=#{companyid}, FATHERID=#{fatherid}, USEREMAIL=#{useremail}, USERMOBILEPHONE=#{usermobilephone}, USERNAME=#{username}, USERPASSWORD=#{userpassword}, USERSTATUS=#{userstatus}, USERTELEPHONE=#{usertelephone}, USERTYPE=#{usertype}, VENDORIDS=#{vendorids}, SAVETIME=NOW(), JOBNUMBER=#{jobnumber}, DEPARTMENT=#{department}, SENDFLAG=#{sendflag}, UFLAG=#{uflag}, UNAME=#{uname}, UFAX=#{ufax}, NUSERTYPE=#{nusertype},
where USERID=#{userid} AND SAVETIME=#{savetime}

mapper.delete:
delete from T_SYS_STAFF
where USERID=#{userid}
AND SAVETIME=#{savetime}

delete from T_SYS_STAFFMENU
where USERID=#{userid}
and AUTHID=#{authid}

mapper.select:
select * from T_SYS_STAFFMENU


        USERID=#{userid}

        AND AUTHID=#{authid}

        AND MENUID IN (${rolepopedom})

select * from T_SYS_ROLE


        AND ROLENAME = #{rolename}

        AND AUTHID = #{authid}

        AND PROVINCEID = #{provinceid}

        AND ROLEID = #{roleid}

mapper.update:
update T_SYS_ROLE

    ROLEPOPEDOM=#{rolepopedom},

where ROLEID=#{roleid}
AND SAVETIME=#{savetime}

mapper.select:
select * from T_SYS_ROLE


        AND ROLENAME = #{rolename}

        AND AUTHID IN (${aaids})

        AND AUTHID =#{authid}

        AND PROVINCEID = #{provinceid}
